def decrypt(cipher, n, lam, mu):
  #cipher = 
  #lam = 
  #n = 
  #mu = 
  n_sq = n * n
  x = pow(cipher, lam, n_sq) - 1
  # print "x = ", x
  # print "pow_cl_modn_sq = ", pow(cipher, lam, n_sq)
  # print "pow_cl_modn_sq-1 = ", pow(cipher, lam, n_sq) - 1
  plain = ((x // n) * mu) % n
  # print "x//n = ", x // n
  return plain


